INSERT INTO tb_review (text, user_id, movie_id) VALUES ('Uma das melhores animações! Muito boa e criativa, boa tanto para crianças quanto para os adultos...', 1, 1);
INSERT INTO tb_review (text, user_id, movie_id) VALUES ('Filme consegue agradar com comédia física,trocadilhos,humor pastelão,piadas sujas,e até humor inteligente,a força do filme está na divisão dos momentos de cada um dos seus personagens carismáticos.', 1, 2);
INSERT INTO tb_review (text, user_id, movie_id) VALUES ('Excelente, um filme sensacional. A direção é ótima, a história é ótima, e a trilha sonora é..... ops, não tem, so percebi ao final do filme, onde você relaxa, toda a tensão do filme acaba e a música vem para te ralaxar. Atuações perfeitas de Javier Bardem e Tommy Lee Jones, e merecidamente o OSCAR de melhor filme. Você tem que assistir esta obra-prima do cinema e se encantar com esse DRAMA épico.', 2, 4);
INSERT INTO tb_review (text, user_id, movie_id) VALUES ('A essência do filme é idêntica à HQ criada por Jack Kirby e Stan Lee,o roteiro provou-se à altura do desafio,honra heróis da Marvel com magnificência e explora os pontos fortes dos seus personagens,sensacional em vários quesitos:ação,aventura,diversão e emoção.', 1, 5);